Big thanks to Rajesh Jinaga for his Stack based approach. I found it really useful for my needs. The following code is a slight modification of Rajesh's method, which processes divisions first, then multiplications, then finishes up with addition and subtraction. It will also allow the use of booleans in the expressions, where true is treated as 1 and false 0. allowing the use of boolean logic in expressions.

public static double Evaluate(string expr)
    {
        expr = expr.ToLower();
        expr = expr.Replace(&quot; &quot;, &quot;&quot;);
        expr = expr.Replace(&quot;true&quot;, &quot;1&quot;);
        expr = expr.Replace(&quot;false&quot;, &quot;0&quot;);

        Stack&lt;String&gt; stack = new Stack&lt;String&gt;();

        string value = &quot;&quot;;
        for (int i = 0; i &lt; expr.Length; i++)
        {
            String s = expr.Substring(i, 1);
            // pick up any doublelogical operators first.
            if (i &lt; expr.Length - 1)
            {
                String op = expr.Substring(i, 2);
                if (op == &quot;&lt;=&quot; || op == &quot;&gt;=&quot; || op == &quot;==&quot;)
                {
                    stack.Push(value);
                    value = &quot;&quot;;
                    stack.Push(op);
                    i++;
                    continue;
                }
            }

            char chr = s.ToCharArray()[0];

            if (!char.IsDigit(chr) &amp;&amp; chr != '.' &amp;&amp; value != &quot;&quot;)
            {
                stack.Push(value);
                value = &quot;&quot;;
            }
            if (s.Equals(&quot;(&quot;))
            {
                string innerExp = &quot;&quot;;
                i++; //Fetch Next Character
                int bracketCount = 0;
                for (; i &lt; expr.Length; i++)
                {
                    s = expr.Substring(i, 1);

                    if (s.Equals(&quot;(&quot;)) bracketCount++;

                    if (s.Equals(&quot;)&quot;))
                    {
                        if (bracketCount == 0) break;
                        bracketCount--;
                    }
                    innerExp += s;
                }
                stack.Push(Evaluate(innerExp).ToString());
            }
            else if (s.Equals(&quot;+&quot;) ||
                     s.Equals(&quot;-&quot;) ||
                     s.Equals(&quot;*&quot;) ||
                     s.Equals(&quot;/&quot;) ||
                     s.Equals(&quot;&lt;&quot;) ||
                     s.Equals(&quot;&gt;&quot;))
            {
                stack.Push(s);
            }
            else if (char.IsDigit(chr) || chr == '.')
            {
                value += s;

                if (value.Split('.').Length &gt; 2)
                    throw new Exception(&quot;Invalid decimal.&quot;);

                if (i == (expr.Length - 1))
                    stack.Push(value);

            }
            else
            {
                throw new Exception(&quot;Invalid character.&quot;);
            }

        }
        double result = 0;
        List&lt;String&gt; list = stack.ToList&lt;String&gt;();
        for (int i = list.Count - 2; i &gt;= 0; i--)
        {
            if (list[i] == &quot;/&quot;)
            {
                list[i] = (Convert.ToDouble(list[i - 1]) / Convert.ToDouble(list[i + 1])).ToString();
                list.RemoveAt(i + 1);
                list.RemoveAt(i - 1);
                i -= 2;
            }
        }

        for (int i = list.Count - 2; i &gt;= 0; i--)
        {
            if (list[i] == &quot;*&quot;)
            {
                list[i] = (Convert.ToDouble(list[i - 1]) * Convert.ToDouble(list[i + 1])).ToString();
                list.RemoveAt(i + 1);
                list.RemoveAt(i - 1);
                i -= 2;
            }
        }
        for (int i = list.Count - 2; i &gt;= 0; i--)
        {
            if (list[i] == &quot;+&quot;)
            {
                list[i] = (Convert.ToDouble(list[i - 1]) + Convert.ToDouble(list[i + 1])).ToString();
                list.RemoveAt(i + 1);
                list.RemoveAt(i - 1);
                i -= 2;
            }
        }
        for (int i = list.Count - 2; i &gt;= 0; i--)
        {
            if (list[i] == &quot;-&quot;)
            {
                list[i] = (Convert.ToDouble(list[i - 1]) - Convert.ToDouble(list[i + 1])).ToString();
                list.RemoveAt(i + 1);
                list.RemoveAt(i - 1);
                i -= 2;
            }
        }
        stack.Clear();
        for (int i = 0; i &lt; list.Count; i++)
        {
            stack.Push(list[i]);
        }
        while (stack.Count &gt;= 3)
        {
            double right = Convert.ToDouble(stack.Pop());
            string op = stack.Pop();
            double left = Convert.ToDouble(stack.Pop());

            if (op == &quot;&lt;&quot;) result = (left &lt; right) ? 1 : 0;
            else if (op == &quot;&gt;&quot;) result = (left &gt; right) ? 1 : 0;
            else if (op == &quot;&lt;=&quot;) result = (left &lt;= right) ? 1 : 0;
            else if (op == &quot;&gt;=&quot;) result = (left &gt;= right) ? 1 : 0;
            else if (op == &quot;==&quot;) result = (left == right) ? 1 : 0;

            stack.Push(result.ToString());
        }
        return Convert.ToDouble(stack.Pop());
    }


